import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Portal.css'
import profileimg from '../../assets/img/profile.svg'

import { withRouter, NavLink } from 'react-router-dom'
class Portal extends Component{
    state = {
        userinfo: null,        
    };

    componentDidMount(){
        console.log(this.props);
        if(this.props.location.state){
            this.setState({
                userinfo: this.props.location.state.userInfo,
            }
            )
        };
    }




    render(){
        let name = 'loading';
        if(this.state.userinfo){
            name = this.state.userinfo.user.sname;
        }
        return(

            <React.Fragment>
                <br />
                <h1>你好，{name}。</h1>
                <br />

                <section className = 'RoleSelect'>
                    <Card style={{ width: '18rem',  }}>
                        <Card.Img variant="top" src={profileimg} width={71} height={95} top='400px' style={{ marginTop: '1em' }} />
                        <Card.Body>
                            <NavLink to='/Portal/Profile'><Button variant="primary">个人信息</Button></NavLink>
                        </Card.Body>
                    </Card>
                    <Card style={{ width: '18rem',  }}>
                        <Card.Img variant="top" src={profileimg} width={71} height={95} top='400px' style={{ marginTop: '1em' }} />
                        <Card.Body>
                            <Button variant="primary">个人信息</Button>
                        </Card.Body>
                    </Card>
                </section>

            </React.Fragment>

        )
    }
}

export default withRouter(Portal);
