import React, { Component } from 'react';
import { withRouter, Redirect } from 'react-router-dom';
import { Form,  Col } from 'react-bootstrap';
import './Profile.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class Profile extends Component {
    state = {
        userinfo: null,
    }

    componentDidMount(){
        console.log(this.props);
        if(this.props.location.state){
            this.setState({
                userinfo: this.props.location.state.userinfo,
            }
            )
        };
    }

    componentDidUpdate(){

    }

    render(){
        let redirect = null;
        if(!this.props.location.state){
            redirect = <Redirect to={{
                pathname: '/',
            }} />;
        }

        let sid = null;

        if(this.state.userinfo){
            sid=this.state.userinfo.user.sid;
        }

        let phone = null;
        if(this.state.userinfo){
            phone = this.state.userinfo.user.pnumber;
        }

        let sclass = null;
        if(this.state.userinfo){
            sclass = this.state.userinfo.user.stuclass;
        }

        console.log(this.props)
        return(

            <React.Fragment>
                {redirect}
                <h1>个人信息</h1>
                <Form>
                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridSID">
                        <Form.Label>用户名</Form.Label>
                        <Form.Control placeholder={sid} disabled />
                        </Form.Group>

                        {/* {<Form.Group as={Col} controlId="formGridPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                        </Form.Group>} */}
                    </Form.Row>

                    <Form.Group controlId="formGridPhone">
                        <Form.Label>电话</Form.Label>
                        <Form.Control placeholder={phone} />
                    </Form.Group>

                    <Form.Group controlId="formGridAddress2">
                        <Form.Label>Address 2</Form.Label>
                        <Form.Control placeholder="Apartment, studio, or floor" />
                    </Form.Group>

                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridCity">
                        <Form.Label>班级</Form.Label>
                        <Form.Control placeholder={sclass} disabled />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridState">
                        <Form.Label>State</Form.Label>
                        <Form.Control as="select">
                            <option>Choose...</option>
                            <option>...</option>
                        </Form.Control>
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridZip">
                        <Form.Label>Zip</Form.Label>
                        <Form.Control />
                        </Form.Group>
                    </Form.Row>

                    <Form.Group id="formGridCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>


                </Form>
            </React.Fragment>
        )
    }
}

export default withRouter(Profile);
