import React, { Component } from 'react';
import { withRouter, Redirect } from 'react-router-dom';
import { Form,  Col, Row, Button } from 'react-bootstrap';
import './Profile.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class Profile extends Component {
    state = {
        userinfo: null,
        isChecked: null,
    }

    componentDidMount(){
        console.log(this.props);
        if(this.props.location.state){
            let gender = true;
            if(this.props.location.state.userinfo.gender === '女'){
                gender = false;
            }
            this.setState({
                userinfo: this.props.location.state.userinfo,
                isChecked: gender,
            }
            )
        };
    }

    phoneChangeHandler = (event) => {
        this.setState({
            userinfo: {
                user: {
                    pnumber: event.target.value,
                }
            }
        })
        console.log(this.state);
    }

    toggleChange = () => {
        if((this.state.userinfo.user.gender === "女" && this.state.isChecked === false) || this.state.isChecked === null){
            this.setState({
                isChecked: true,
                userinfo: {
                    ...this.state.userinfo,
                    user: {
                        ...this.state.userinfo.gender,
                        gender: '男',
                    }
                }
            })
        }
        else if(this.state.userinfo.user.gender === "男" && this.state.isChecked === true){
            this.setState({
                isChecked: false,
                userinfo: {
                    ...userinfo,
                    user: {...user,
                        gender: '女',
                    }
                }
            })
        }
        console.log(this.state);
      }

    render(){
        let redirect = null;
        if(!this.props.location.state){
            redirect = <Redirect to={{
                pathname: '/',
            }} />;
        }

        let sid = 'null';

        if(this.state.userinfo){
            sid=this.state.userinfo.user.sid;
        }

        let phone = 'null';
        if(this.state.userinfo){
            phone = this.state.userinfo.user.pnumber;
        }

        let sclass = 'null';
        if(this.state.userinfo){
            sclass = this.state.userinfo.user.stuclass;
        }

        let pos = 'null';
        if(this.state.userinfo){
            pos = this.state.userinfo.user.mposition;
        }

        let grp = '未分组';
        if(this.state.userinfo){
            grp = this.state.userinfo.user.dgroup;
        }

        let name = 'null';
        if(this.state.userinfo){
            name = this.state.userinfo.user.sname;
        }

        console.log(this.props)
        return(

            <React.Fragment>
                {redirect}
                <h1>个人信息</h1>
                <Form>
                    <Form.Row>
                        <Form.Group as={Col} controlId="formSID">
                        <Form.Label>用户名</Form.Label>
                        <Form.Control value={sid} disabled />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formName">
                        <Form.Label>姓名</Form.Label>
                        <Form.Control value={name} disabled />
                        </Form.Group>

                        {/* {<Form.Group as={Col} controlId="formGridPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                        </Form.Group>} */}
                    </Form.Row>

                    <Form.Group controlId="formPhone">
                        <Form.Label>电话</Form.Label>
                        <Form.Control defaultValue={phone} onChange={(event) => this.phoneChangeHandler(event)} />
                    </Form.Group>

                    <Form.Row>
                        <Form.Group as={Col} controlId="formPosition">
                            <Form.Label>职务</Form.Label>
                            <Form.Control value={pos} disabled />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGroup">
                            <Form.Label>分组</Form.Label>
                            <Form.Control value={grp} disabled />
                        </Form.Group>
                    </Form.Row>

                    <Form.Row>
                        <Form.Group as={Col} sm controlId="formClass">
                        <Form.Label>班级</Form.Label>
                        <Form.Control value={sclass} disabled />
                        </Form.Group> 
                    </Form.Row>


                    <Form.Row>
                    <Form.Group as={Col} sm id="formGender">
                        <Form.Label>性别</Form.Label>
                        <fieldset>
                        <Row>
                            <Form.Check
                            type="radio"
                            label="男"
                            name="radioMale"
                            id="radioMale"
                            checked={this.state.isChecked}
                            onChange={this.toggleChange}
                            />
                            <Form.Check
                            type="radio"
                            label="女"
                            name="radioFemale"
                            id="radioFemale"
                            checked={!this.state.isChecked}
                            onChange={this.toggleChange}
                            />
                        </Row>
                        </fieldset>

                    </Form.Group>
                    </Form.Row>
                </Form>
                <Button id= 'submit' variant="primary" type="submit">
                    提交
                </Button>
            </React.Fragment>
        )
    }
}

export default withRouter(Profile);
