import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './LoginForm.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Alert from 'react-bootstrap/Alert';




class LoginForm extends Component {

    state = {
        username: null,
        password: null,
    }

    usernameChangeHandler = () => {
        this.setState({
            username: this.target.value,
        })

        console.log(this.state);
    }

    render() {
        return (
            <div>
                <Form>
                    <Form.Group controlId="formBasicEmail">
                        <h1>登录 - 晨跑管理系统</h1>
                        <h1> </h1>

                        <Form.Control type="text" placeholder="学号" onChange = {this.usernameChangeHandler} />
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Control type="password" placeholder="密码" />
                    </Form.Group>
                        <Button block variant="primary" type="submit">
                            提交
                        </Button>
                        <p></p>
                        <Form.Text className="text-muted" >
                            忘记密码?
                        </Form.Text>
                </Form>

                <Container>
                    <Row className="justify-content-md-center">
                    <Col xs={10}><Alert variant = 'warning' id = 'alertWarn'> The site is still under constuction. </Alert></Col>
                    </Row>
                </Container>
            </div>
        )
    }
}

export default loginForm;
